mapper.update:
update t_p_preferential_activity
name = #{name}, name_first_letter = #{name_first_letter}, color = #{color}, starttime = #{starttime}, endtime = #{endtime}, apply_all = #{apply_all}, creator = #{creator}, activity_introduction = #{activity_introduction}, use_notice = #{use_notice}
where id = #{id}

update t_p_preferential_detail
free_reason = #{free_reason}, discount = #{discount}, starttime = #{starttime}, endtime = #{endtime},
can_credit = #{can_credit}, amount = #{amount}
where id = #{id}
