mapper.select:
select date,type,jdeNo,branchId,branchName,sum(shouldamount) as shouldamount,sum(paidinamount) as paidinamount,sum(discountamount) as discountamount,sum(card) as card,sum(othercard) as othercard,sum(weixin) as weixin,sum(zhifubao) as zhifubao,sum(cash) as cash,sum(credit) as credit,sum(merbervaluenet) as merbervaluenet,sum(mebervalueadd) as mebervalueadd,sum(meberTicket) as meberTicket,sum(integralconsum) as integralconsum,sum(meberTicket) as meberTicket from t_branch_business_info where date  >= #{beginTime} and date  <= #{endTime} and branchId in

  #{item}


  and  type in (0,1)


  and  type =#{type}

group by branchId order by branchId

select id,date,type,jdeNo,branchId,branchName,sum(shouldamount) as shouldamount,sum(paidinamount) as paidinamount,sum(discountamount) as discountamount,sum(card) as card,sum(othercard) as othercard,sum(weixin) as weixin,sum(zhifubao) as zhifubao,sum(cash) as cash,sum(credit) as credit,sum(merbervaluenet) as merbervaluenet,sum(mebervalueadd) as mebervalueadd,sum(meberTicket) as meberTicket,sum(integralconsum) as integralconsum,sum(meberTicket) as meberTicket from t_branch_business_info where  date  >= #{beginTime} and date  <= #{endTime} and branchId in

  #{item}

 
  and  type in (0,1)


  and  type =#{type}

group by date,branchId order by date desc
