mapper.select:
select name from t_b_employee tbe,t_b_user tbu where tbe.user_id = tbu.id

   and tbe.branch_id = #{branchId}
 
 and job_number = #{userId}

SELECT too.orderid, IFNULL(too.custnum,0) as personnum, DATE_FORMAT(too.begintime,'%Y-%m-%d %H:%i:%s') as begintime, DATE_FORMAT(too.endtime,'%Y-%m-%d %H:%i:%s') as endtime, tt.tableNo as tableno, tta.areaname as area, tbu.name as waiter FROM t_order too, t_table tt, t_tablearea tta, t_b_employee tbe, t_b_user tbu WHERE too.currenttableid = tt.tableid AND tt.areaid = tta.areaid AND tbe.user_id = tbu.id AND tbe.job_number = too.userid
AND too.orderid = #{orderid}
and tbe.branch_id = #{branchId}

SELECT td.title AS itemdesc, sum(tod.dishnum) AS count, tod.dishunit AS dishunit, IFNULL(round(tod.orderprice,2), 0) AS price, IFNULL(round(tod.orderprice * sum(tod.dishnum),2),0) AS amount FROM t_order_detail tod, t_dish td WHERE tod.dishid = td.dishid
AND tod.orderid = #{orderid}
group by tod.dishid,tod.dishunit,tod.orderprice

SELECT IFNULL(a.bankcardno,'') AS bankcardno, IFNULL(sum(payamount),0) AS amount, itemDesc AS name FROM t_settlement_detail a LEFT JOIN ( SELECT itemid, itemDesc FROM t_dictionary WHERE type = 'PAYWAY' ) b ON (a.payway = b.itemid) WHERE a.payway <> 7 AND a.payamount <> 0 AND a.payway <> 20
AND a.orderid = #{orderid}
GROUP BY orderid, couponNum, incometype, payway, itemDesc, membercardno, bankcardno

select IFNULL(totalconsumption,0) as totalconsumption, IFNULL(payway,0) as payway, IFNULL(payamount,0) as payamount, couponamount, IFNULL(giveamount,0) as giveamount, IFNULL(paidamount,0) as paidamount, invoiceamount from (select sum(tsd.payamount) as totalconsumption, (select tp.payway from t_settlement_detail tp where tp.orderid = #{orderid} and tp.payway in (7,20)) as payway, (select tp.payamount from t_settlement_detail tp where tp.orderid = #{orderid} and tp.payway in (7,20)) as payamount, IFNULL(sum(tsd.couponamount),0) AS couponamount, (select sum(orignalprice) from t_order_detail where orderid = #{orderid} and pricetype = 1) AS giveamount, (select sum(tp.payamount) from t_settlement_detail tp where tp.orderid = #{orderid} and tp.payway in (0,1,5,8,13,17,18)) as paidamount, 0.00 AS invoiceamount from t_settlement_detail tsd where 1=1
AND tsd.orderid = #{orderid}
group by tsd.orderid) torder
