mapper.select:
SELECT tsdh.orderid, too.endtime as before_cleartime, (select IFNULL(sum(tod.dishnum*tod.orignalprice),0) from t_order_detail_history tod where tod.orderid = too.orderid) -(SELECT IFNULL(sum(dishnum*orignalprice),0) FROM t_order_detail_history WHERE orderid = too.orderid and dishtype = 2 and superkey = primarykey) as before_shouldamount, IFNULL(sum(tsdh.payamount),0)-IFNULL(tsh.Inflated,0) as before_paidamount, too.userid as waiter, tsh.userid as cashier, tsh.authorizer_name FROM t_settlement_detail_history tsdh, t_order_history too, t_settlement_history tsh WHERE tsdh.orderid = too.orderid AND tsdh.orderid = tsh.orderid AND too.branchid = #{branchId}
AND too.endtime BETWEEN str_to_date(#{beginTime},'%Y-%m-%d %H:%i:%s') AND str_to_date(#{endTime},'%Y-%m-%d %H:%i:%s')
AND tsh.reason not in('??????,??????' ,'会员结算失败,系统自动反结','会员结算失败，系统自动反结') AND tsdh.payway in (0,1,5,8,13,17,18) AND too.orderstatus = 3 group by tsdh.orderid

select name from t_b_employee tbe,t_b_user tbu where tbe.user_id = tbu.id

   and tbe.branch_id = #{branchId}
 
 and job_number = #{userId}

SELECT td.title AS itemdesc, round(sum(tod.dishnum)) AS count, tod.dishunit AS dishunit, IFNULL(round(tod.orderprice,2), 0.00) AS price, IFNULL(round(tod.orderprice * sum(tod.dishnum),2),0.00) AS amount FROM t_order_detail tod, t_dish td WHERE tod.dishid = td.dishid
AND tod.orderid = #{orderid}
group by tod.dishid,tod.dishunit,tod.orderprice

SELECT IFNULL(a.bankcardno,'') AS bankcardno, IFNULL(sum(payamount),0) AS amount, itemDesc AS name FROM t_settlement_detail a LEFT JOIN ( SELECT itemid, itemDesc FROM t_dictionary WHERE type = 'PAYWAY' ) b ON (a.payway = b.itemid) WHERE a.payway <> 7 AND a.payamount <> 0 AND a.payway <> 20
AND a.orderid = #{orderid}
GROUP BY orderid, couponNum, incometype, payway, itemDesc, membercardno, bankcardno
