mapper.select:
SELECT tdc.itemDesc, DATE_FORMAT(tsd.inserttime,'%Y-%m-%d %H:%i:%s') as insertTime, tsd.orderid, IFNULL(tsd.payamount,0) as payAmount, tsd.membercardno, tdc.itemid FROM t_dictionary tdc, t_settlement_detail tsd, t_order too WHERE tdc.itemid = tsd.payway AND tsd.orderid = too.orderid AND tdc.type = 'PAYWAY' AND tsd.payamount > 0
and too.branchid = #{branchId}
and too.shiftid = #{shiftid}
and tdc.itemid = #{itemid}
and too.begintime >= str_to_date(#{beginTime},'%Y-%m-%d %H:%i:%s')
and too.begintime <= str_to_date(#{endTime},'%Y-%m-%d %H:%i:%s')
and tsd.membercardno = #{membercardno}
order by tsd.inserttime asc

SELECT tdc.itemDesc as payWayName, IFNULL(count(tsd.sdetailid),0) as nums, IFNULL(sum(tsd.payamount),0) as price, tsd.membercardno, tdc.itemid FROM t_dictionary tdc, t_settlement_detail tsd, t_order too WHERE tdc.itemid = tsd.payway AND tsd.orderid = too.orderid AND tdc.type = 'PAYWAY' AND tsd.payamount > 0
and too.branchid = #{branchId}
and too.shiftid = #{shiftid}
and tdc.itemid = #{itemid}
and str_to_date(too.begintime,'%Y-%m-%d %H:%i:%s') >= str_to_date(#{beginTime},'%Y-%m-%d %H:%i:%s')
and str_to_date(too.begintime,'%Y-%m-%d %H:%i:%s') <= str_to_date(#{endTime},'%Y-%m-%d %H:%i:%s')
and tsd.membercardno = #{membercardno}
order by tsd.inserttime asc
