mapper.insert:
	   insert into t_dish (
	     dishid,
		 price,
         vipprice
	   )values (
	     #{dishid},
	     #{price},
	     #{vipprice}
	   )

mapper.select:
select
dictid,
itemid,
itemDesc,
itemSort,
status,
type,
typename,
item_value
from t_dictionary

    
        dictid=#{id}
    
    
        and itemid=#{itemid}
    
    
        and itemDesc=#{itemDesc}
    
    
        and itemSort=#{itemSort}
    
    
        and status=#{status}
    
    
        and type=#{type}
    
    
        and typename=#{typename}

select
dictid,
itemid,
itemDesc,
itemSort,
status,
type,
typename
from t_dictionary

    and status = 1
    
        and dictid=#{id}
    
    
        and itemid=#{itemid}
    
    
        and itemDesc=#{itemDesc}
    
    
        and itemSort=#{itemSort}
    
    
        and type=#{type}
    
    
        and typename=#{typename}
    
    order by type asc ,itemid asc
