mapper.select:
select 
  id,
  userId,
  targetId,
  operationtType,
  address,
  userAgent,
  createTime
from tb_operation

  
     id=#{id}
  
  
     and userId=#{userid}
  
  
     and targetId=#{targetid}
  
  
     and operationtType=#{operationttype}
  
  
     and address=#{address}
  
  
     and userAgent=#{useragent}
  
  
     and createTime=#{createtime}

