        ( `status` is null or status !='1')

			AND tpa.id = #{id}


			AND tpa.code = #{code}


			AND tpa.name = #{name}


			AND tpa.name_first_letter LIKE '%${nameFirstLetter}%'


			and (tpa.name like '%${fullName}%' or tpa.name_first_letter like '%${fullName}%')


		        AND tpa.type = #{type}


		     AND tpa.type not in(01,02,03,04,05,06,0601,0602)


			AND tpa.sub_type = #{subType} 


			AND tpa.color = #{color}


			AND  tpa.starttime <= #{currentTime} AND tpa.endtime >= #{currentTime} 


			AND  tpa.starttime <= #{starttime} 


			AND  tpa.endtime <= #{endtime}

        det.preferential not in (
        select id from t_p_preferential_activity where `status` ='1')


			AND det.id = #{id} 


			AND det.preferential = #{preferential} 


			AND det.dish = #{dish} 


			AND det.dish_title = #{dish_title} 


			AND det.free_reason = #{free_reason} 


			AND  det.starttime <= #{starttime} 


			AND  det.endtime <= #{endtime}

mapper.select:
SELECT id, code, name, name_first_letter, type, type_name, subtable_name, sub_type, sub_type_name, color, starttime, endtime, apply_all, activity_introduction, use_notice, createtime, creator FROM ( SELECT * FROM t_p_preferential_activity tpa where tpa.apply_all='1' UNION SELECT * FROM t_p_preferential_activity tpa2 where tpa2.apply_all='0' and tpa2.id in ( select preferential from t_p_preferential_branch where branch in ( #{branch_ids}) ) ) AS newtpa
AND newtpa.id = #{id}
AND newtpa.code = #{code}
AND newtpa.name = #{name}
AND newtpa.name_first_letter LIKE '%${nameFirstLetter}%'
and (newtpa.name like '%${fullName}%' or newtpa.name_first_letter like '%${fullName}%')
AND newtpa.type = #{type}
AND newtpa.type not in(01,02,03,04,05,06,0601,0602)
AND newtpa.sub_type = #{subType}
AND newtpa.color = #{color}
AND newtpa.starttime <= #{currentTime} AND newtpa.endtime >= #{currentTime}
AND newtpa.starttime <= #{starttime}
AND newtpa.endtime <= #{endtime}
ORDER BY code

SELECT code, name, is_subtype, parent_type
FROM t_p_preferential_type_dict


		AND is_subtype = #{isSubType}


		AND parent_type = #{parentType}


ORDER BY code

SELECT code, name, is_subtype as isSubType, parent_type as parentType, subtable_name as subtableName
FROM t_p_preferential_type_dict
WHERE code = #{code}

SELECT id,preferential,bill_amount,amount as token_amount 
FROM t_p_preferential_detail
WHERE preferential=#{preferential_id}

choose.otherwise:
SELECT det.id, det.preferential, det.dish, det.dish_title, det.price, det.sequence, det.unit, det.unitflag, det.discount, det.amount, det.bill_amount, det.free_reason, det.company_name, det.company_first_letter, det.can_credit, tpa.code, tpa.name, tpa.name_first_letter, tpa.type, tpa.type_name, tpa.subtable_name, tpa.sub_type, tpa.sub_type_name, tpa.color, tpa.apply_all, tpa.activity_introduction, tpa.use_notice, tpa.starttime, tpa.endtime FROM t_p_preferential_detail det ,t_p_preferential_activity tpa WHERE 1=1 and (tpa.`status` is null or tpa.status !='1')
AND tpa.type = #{type}
and det.free_reason not in('2')
and tpa.apply_all=1
and det.preferential=#{preferential}
AND det.preferential = tpa.id
UNION SELECT det.id, det.preferential, det.dish, det.dish_title, det.price, det.sequence, det.unit, det.unitflag, det.discount, det.amount, det.bill_amount, det.free_reason, det.company_name, det.company_first_letter, det.can_credit, tpa.code, tpa.name, tpa.name_first_letter, tpa.type, tpa.type_name, tpa.subtable_name, tpa.sub_type, tpa.sub_type_name, tpa.color, tpa.apply_all, tpa.activity_introduction, tpa.use_notice, tpa.starttime, tpa.endtime FROM t_p_preferential_detail det ,t_p_preferential_activity tpa WHERE 1=1 AND det.preferential = tpa.id and (tpa.`status` is null or tpa.status !='1')
AND tpa.type = #{type}
and det.free_reason not in('2')
and det.preferential=#{preferential}
and tpa.apply_all=0 and exists ( select * from t_p_preferential_branch where branch=#{branchid} and preferential=tpa.id )
ORDER BY preferential , sequence
