mapper.insert:
insert into t_printer ( printerid, printerNo, printername, printertype, createuserid, inserttime, ipaddress, port, status
)values ( #{printerid}, #{printerNo}, #{printername}, #{printertype}, #{createuserid}, #{inserttime}, #{ipaddress}, #{port}, #{status}
)

mapper.select:
	select
		printerid,
		printerNo,
		printername,
		printertype,
		createuserid,
		inserttime,
		ipaddress,
		port,
		status,
		printNum
		from t_printer
		
		 
   		status=1
     
        printerid=#{printerid}
     
     
        and printerNo=#{printerNo}
     
     
        and printername=#{printername}
     
     
        and printertype=#{printertype}
     
     
        and createuserid=#{createuserid}
     
     
        and inserttime=#{inserttime}
     
     
        and ipaddress=#{ipaddress}
     
     
        and port=#{port}
     
     
     
        and status=#{status}
     
   
		order by printerNo asc

		select
			printerid,
			printerNo,
			printername,
			printertype,
			createuserid,
			inserttime,
			ipaddress,
			port,
			status,
			printNum,
			from t_printer

			 
	     
			 status=#{status}
	     
				 
	        printerid=#{printerid}
	     
	     
	        and printerNo=#{printerNo}
	     
	     
	        and printername=#{printername}
	     
	     
	        and printertype=#{printertype}
	     
	     
	        and createuserid=#{createuserid}
	     
	     
	        and inserttime=#{inserttime}
	     
	     
	        and ipaddress=#{ipaddress}
	     
	     
	        and port=#{port}
	     

	     
	        and status=#{status}
	     
	   
			order by printerNo asc

select
printerid,
printerNo,
printername,
printertype,
createuserid,
inserttime,
ipaddress,
port,
status,
printNum
from t_printer

	
		and printerid=#{printerid}
	
	
		and printerNo=#{printerNo}
	
	
		and printername=#{printername}
	
	
		and printertype=#{printertype}
	
	
		and createuserid=#{createuserid}
	
	
		and inserttime=#{inserttime}
	
	
		and ipaddress=#{ipaddress}
	
	
		and port=#{port}
	
	
		and status=#{status}
