mapper.select:
select 
  resourcesId,
  resourcesPid,
  resourcesName,
  resourcesPath,
  resourcesDesc,
  resourcesType,
  indexValue,
  status,
  createTime
from t_resource

  
     resourcesId=#{resourcesid}
  
  
     and resourcesPid=#{resourcespid}
  
  
     and resourcesName=#{resourcesname}
  
  
     and resourcesPath=#{resourcespath}
  
  
     and resourcesDesc=#{resourcesdesc}
  
  
     and resourcesType=#{resourcestype}
  
  
     and indexValue=#{indexvalue}
  
  
     and status=#{status}
  
  
     and createTime=#{createtime}
  

 order by indexValue asc

select 
  resourcesId,
  resourcesPid,
  resourcesName,
  resourcesPath,
  resourcesDesc,
  resourcesType,
  indexValue,
  status,
  createTime
from t_resource

  
     resourcesId=#{resourcesid}
  
  
     and resourcesPid=#{resourcespid}
  
  
     and resourcesName=#{resourcesname}
  
  
     and resourcesPath=#{resourcespath}
  
  
     and resourcesDesc=#{resourcesdesc}
  
  
     and resourcesType=#{resourcestype}
  
  
     and indexValue=#{indexvalue}
  
  
     and status=#{status}
  
  
     and createTime=#{createtime}

	   	   select 
	      tr.resourcesId ,tr.resourcesPath
	   from t_resource tr,
          t_resource_role trr, 
           t_user_role tu
     where 
        tu.roleId = trr.roleid
        and trr.resourceId = tr.resourcesId
	     and tu.userid = #{userid}
	        and resourcesPath=#{resourcespath}
