mapper.select:
select d.dishid, d.columnId,t.itemDesc, d.userId, d.title, d.label, d.introduction, d.source, d.author, d.image, d.imageTitle, d.content, d.isDisplay, d.isHead, d.headSort, d.isSelect, d.createTime, d.releaseTime, d.modifyTime, d.status, d.price,d.vipprice, d.dishNo, d.level, p.printername printer, d.unit, d.abbrdesc, d.dishtype,d.orderNum from t_dish d LEFT JOIN t_basicdata t on d.columnId=t.id LEFT JOIN t_printer p on d.printer=p.printerid
and d.status=1
and source=#{source}
and title like CONCAT('%',#{title},'%')
and isSelect=#{isselect}

		SELECT t.* , td.columnid,tb.itemDesc from t_dish t,t_dish_dishtype td ,t_basicdata tb
	

	
 		and t.dishid=td.dishid AND td.columnid = tb.id and tb.status = 1
	  
       and dishid = #{dishid}
      
      
        and columnId = #{columnId}
      
      
        and userId = #{userId}
      
      
        and title = #{title}
      
      
        and label = #{label}
      
      
        and introduction = #{introduction}
      
      
        and source = #{source}
      
      
        and author = #{author}
      
      
        and image = #{image}
      
      
       and imageTitle = #{imageTitle}
      
      
        and content = #{content}
      
      
        and isDisplay = #{isDisplay}
      
      
        and isHead = #{isHead}
      
      
        and headSort = #{headSort}
      
      
        and isSelect = #{isSelect}
      
      
        and createTime = #{createTime}
      
      
        and releaseTime = #{releaseTime}
      
      
        and modifyTime = #{modifyTime}
      
      
        and status = #{status}
      
      
        and price = #{price}
      
      
        and vipprice = #{vipprice}
      
      
        and dishNo = #{dishNo}
      
      
        and level = #{level}
      
      
        and printer = #{printer}
      
      
        and unit = #{unit}
      
      
        and abbrdesc = #{abbrdesc}
      
      
        and dishtype = #{dishtype}
      
      
        and orderNum = #{orderNum}

select t.printer,t.dishid, t.columnId, t.userId, t.title, t.label, t.introduction, t.source, t.author, t.image, t.imageTitle, t.content, t.isDisplay, t.isHead, t.headSort, 
t.isSelect, t.createTime, t.releaseTime, t.modifyTime, t.status, t.price,t.vipprice, t.dishNo, t.level, t.unit, t.abbrdesc, t.dishtype,t.orderNum,t.recommend,t.weigh 
from t_dish t  where t.dishid = #{id,jdbcType=VARCHAR}

