mapper.select:
select td.dishid ,td.ishead, CONCAT_WS('#',tp.ipaddress,tp.port) content, td.title from t_printer tp ,t_dish td where tp.printerid = td.printer and td.dishid in (
#{articleid}
)

	SELECT t.* from t_dish t,t_dish_dishtype td where td.columnid=#{columnid,jdbcType=VARCHAR}
	and t.dishid=td.dishid
    ORDER BY t.createTime asc

SELECT t.dishid, t.columnId, t.userId, t.title, t.label, t.introduction, t.source, t.author, t.image, t.imageTitle,t.content, t.isDisplay, t.isHead, t.headSort, t.isSelect, t.createTime, t.releaseTime, t.modifyTime,t.status, t.price,t.vipprice, t.dishNo, t.level, t.printer, t.unit, t.abbrdesc, t.dishtype,t.orderNum,tb.itemDesc from t_dish t,t_dish_dishtype td,t_basicdata tb
and t.dishid=td.dishid and tb.status = 1 and t.source = tb.id
and td.columnid=#{columnid,jdbcType=VARCHAR}
and td.dishid=#{dishid,jdbcType=VARCHAR}
and (t.title like '%${fullName}%' )
ORDER BY t.createTime asc

