mapper.select:
select

from t_order_detail
where orderid = #{orderid,jdbcType=VARCHAR}
and
dishid = #{dishid,jdbcType=VARCHAR}

select

from t_order_detail

	
		and orderid=#{orderid}
	
	
		and dishid=#{dishid}
	
	
		and dishstatus=#{dishstatus}
	
	
		and begintime=#{begintime}
	
	
		and sperequire=#{sperequire}
	
	
		and dishnum=#{dishnum}
	
	
		and userName=#{userName}
	
	
		and orderprice=#{orderprice}
	
	
		and discountrate=#{discountrate}
	
	
		and discountamount=#{discountamount}
	
	
		and fishcode=#{fishcode}
	
	
		and dishtype=#{dishtype}
	
	
		and dishunit=#{dishunit}
	
	
		and orignalprice = #{orignalprice}
	
	
		and pricetype = #{pricetype}
	
	
		and relatedishid = #{relatedishid}
	
	
		and ismaster = #{ismaster}
	
	
		and parentkey = #{parentkey}

select t.orderprice,t.dishunit,t.primarykey,t.dishid,
case when
t.pricetype=2 then concat('(礼)',IFNULL(f.dishname,d.title)) else
IFNULL(f.dishname,d.title) end as dishname,
t.userName,t.orderid,t.orderseq,
t.dishtype,t.ispot,t.dishnum,t.sperequire,t.dishstatus,t.islatecooke,
t.pricetype,t.sperequire
from t_order_detail t LEFT JOIN t_dish d on
t.dishid=d.dishid
left join (select max(dishname) as dishname,dishid
from t_template_dishunit where menuid=(select tm.menuid from
t_branch_info tbf,t_menu_branch mb,t_menu tm where tbf.branchid =
mb.branchid and mb.menuid = tm.menuid and tm.status ='1' order by
tm.effecttime desc limit 1) group by dishid) f on(t.dishid=f.dishid)

	
		and t.orderid=#{orderid}
	
	
		and t.dishid=#{dishid}
	
	
		and t.dishtype=#{dishtype}
	
	
		and t.ismaster = #{ismaster}
	
	
		and t.parentkey = #{parentkey}
	
	
		and t.childdishtype = #{childdishtype}

