mapper.select:
SELECT ord.orderid,ord.userid,ord.custnum,us.`name`,sum(ordt.dishnum*ordt.orignalprice) as shouldamount, tab.tableNo as tableids, t.xj, t.yhk, t.ml, t.hykxf, t.hyjfxf, t.gz2, t.wxzf, t.zfbzf from t_order ord LEFT JOIN t_order_detail ordt ON ord.orderid = ordt.orderid left join t_b_employee em on ord.userid = em.job_number left join t_b_user us on us.id = em.user_id LEFT JOIN t_table tab ON tab.tableid = ord.currenttableid LEFT JOIN (SELECT orderid, MAX(CASE payway WHEN 0 THEN payamount ELSE 0 END ) xj, MAX(CASE payway WHEN 1 THEN payamount ELSE 0 END ) yhk, MAX(CASE payway WHEN 7 THEN payamount ELSE 0 END ) ml, MAX(CASE payway WHEN 8 THEN payamount ELSE 0 END ) hykxf, MAX(CASE payway WHEN 11 THEN payamount ELSE 0 END ) hyjfxf, (MAX(CASE payway WHEN 5 THEN payamount ELSE 0 END ) +MAX(CASE payway WHEN 13 THEN payamount ELSE 0 END ) ) gz2, (MAX(CASE payway WHEN 17 THEN payamount ELSE 0 END ) +MAX(CASE payway WHEN 30 THEN payamount ELSE 0 END )) wxzf, MAX(CASE payway WHEN 18 THEN payamount ELSE 0 END ) zfbzf FROM t_settlement_detail GROUP BY orderid) t ON ord.orderid = t.orderid where ord.begintime >= #{beginTime} and ord.begintime <= #{endTime} and (ordt.primarykey <> ordt.superkey or ordt.ordertype <> 2) and em.branch_id = #{branchId} and ord.branchid = #{branchId} and ord.orderstatus=3
and ord.shiftid in (0,1)
and ord.shiftid =#{shiftid}
and ord.userid =#{userid}
group by ord.orderid

       select ord.orderid,sum(setd.payamount) as paidinamount from t_order ord left join t_settlement_detail setd on ord.orderid = setd.orderid where setd.payway in (0,1,5,8,13,17,18,30)
       and ord.begintime >= #{beginTime} and ord.begintime <= #{endTime} and ord.branchid = #{branchId}  and ord.orderstatus=3
        
	      and  ord.shiftid in (0,1)
	    
	    
	      and  ord.shiftid =#{shiftid}
	    
	    
	      and  ord.userid =#{userid}
	    
       group by ord.orderid

      SELECT ord.orderid, sum(mem.Inflated) as inflated from t_order ord LEFT JOIN t_order_member mem ON ord.orderid = mem.orderid
       where ord.begintime >= #{beginTime} and ord.begintime <= #{endTime} and ord.branchid = #{branchId}  and ord.orderstatus=3
        
	      and  ord.shiftid in (0,1)
	    
	    
	      and  ord.shiftid =#{shiftid}
	    
	   
	      and  ord.userid =#{userid}
	    
	    GROUP BY
		ord.orderid;
